import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const questions = [
  {
    id: 1,
    question:
      "If you have a bowl with 6 apples and you take away 3, how many do you have left?",
    options: ["a) 3", "b) 6", "c) 2", "d) 4"],
    answer: "a) 3",
  },
  {
    id: 2,
    question: "A farmer has 15 cows and all but 8 die. How many cows are left?",
    options: ["a) 7", "b) 8", "c) 15", "d) 23"],
    answer: "b) 8",
  },
  {
    id: 3,
    question:
      'Which word in the following sentence is spelled incorrectly? "The quick brown fox jumps over the lazy dog."',
    options: [
      "a) quick",
      "b) brown",
      "c) fox",
      "d) All words are spelled correctly",
    ],
    answer: "d) All words are spelled correctly",
  },
  {
    id: 4,
    question:
      'If you rearrange the letters "DEBIT CARD," you would have a new phrase. What is it?',
    options: [
      "a) Bad Credit",
      "b) Bad Cider",
      "c) Bad Cider Tad",
      "d) Bad Cart Ride",
    ],
    answer: "b) Bad Cider",
  },
  {
    id: 5,
    question:
      "If you have a basket with 5 eggs and you take away 3, how many will be left?",
    options: ["a) 2", "b) 3", "c) 5", "d) 8"],
    answer: "a) 2",
  },
  {
    id: 6,
    question: "What word becomes shorter when you add two letters to it?",
    options: ["a) Short", "b) Longer", "c) Shorter", "d) None of the above"],
    answer: "c) Shorter",
  },
  {
    id: 7,
    question:
      'Which word in the following sentence is spelled incorrectly? "He gave me a seperate piece of cake."',
    options: ["a) He", "b) gave", "c) me", "d) seperate"],
    answer: "d) seperate",
  },
  {
    id: 8,
    question:
      "If you have a basket with 10 apples and you take away 3, how many will be left?",
    options: ["a) 7", "b) 8", "c) 9", "d) 10"],
    answer: "a) 7",
  },
  {
    id: 9,
    question:
      "What is the next letter in the following series? O T T F F S S ?",
    options: ["a) E", "b) S", "c) F", "d) N"],
    answer: "c) F",
  },
  {
    id: 10,
    question:
      'If you rearrange the letters "LISTEN," you would have a new phrase. What is it?',
    options: ["a) Silent", "b) Tinsel", "c) Enlist", "d) Inlets"],
    answer: "c) Enlist",
  },
];

const Quiz = () => {
  const location = useLocation();
  const { duration = 10 } = location.state || {};
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [timeRemaining, setTimeRemaining] = useState(duration * 60); // in seconds
  const [answers, setAnswers] = useState(Array(questions.length).fill(""));
  const [score, setScore] = useState(0);
  const [quizState, setQuizState] = useState([]);
  const [questionStatus, setQuestionStatus] = useState(
    Array(questions.length).fill("notAttempted")
  );

  useEffect(() => {
    const savedAnswers =
      JSON.parse(localStorage.getItem("answers")) ||
      Array(questions.length).fill("");
    setAnswers(savedAnswers);
  }, []);

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeRemaining((prevTime) => {
        if (prevTime === 0) {
          clearInterval(timer);
          handleNextQuestion(true);
          return 0;
        } else {
          return prevTime - 1;
        }
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    localStorage.setItem("answers", JSON.stringify(answers));
  }, [answers]);

  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const handleNextQuestion = (missed = false) => {
    const correctAnswer = questions[currentQuestion].answer;
    const isCorrect = selectedOption === correctAnswer;
    const marks = isCorrect ? 1 : 0;

    setAnswers((prevAnswers) => {
      const updatedAnswers = [...prevAnswers];
      updatedAnswers[currentQuestion] = selectedOption;
      return updatedAnswers;
    });

    setQuizState((prevQuizState) => [
      ...prevQuizState,
      {
        question: questions[currentQuestion].question,
        selectedOption,
        correctAnswer,
        marks,
      },
    ]);

    setScore((prevScore) => prevScore + marks);

    const updatedStatus = [...questionStatus];
    updatedStatus[currentQuestion] = missed ? "missed" : "attempted";
    setQuestionStatus(updatedStatus);

    setSelectedOption("");

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion((prevQuestion) => prevQuestion + 1);
    }
  };

  return (
    <div className="quiz-container">
      <div className="question-container">
        <p>{questions[currentQuestion].question}</p>
        <ul>
          {questions[currentQuestion].options.map((option, index) => (
            <li key={index}>
              <input
                type="radio"
                id={`option${index}`}
                name="options"
                value={option}
                checked={selectedOption === option}
                onChange={handleOptionChange}
              />
              <label htmlFor={`option${index}`}>{option}</label>
            </li>
          ))}
        </ul>
      </div>
      <div className="timer">
        <p>
          Time Remaining: {Math.floor(timeRemaining / 60)}:
          {("0" + (timeRemaining % 60)).slice(-2)}
        </p>
      </div>
      <button onClick={() => handleNextQuestion()}>Next</button>
      {timeRemaining === 0 && <p>Time's up! Moving to the next question...</p>}
      {currentQuestion === questions.length - 1 && (
        <button onClick={() => console.log("Quiz completed!")}>
          Finish Quiz
        </button>
      )}
      <p>Current Score: {score}</p>
    </div>
  );
};

export default Quiz;
