import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const questions = [
  {
    question:
      "If you have a bowl with 6 apples and you take away 3, how many do you have left?",
    options: ["a) 3", "b) 6", "c) 2", "d) 4"],
    answer: "a) 3",
  },
  {
    question: "A farmer has 15 cows and all but 8 die. How many cows are left?",
    options: ["a) 7", "b) 8", "c) 15", "d) 23"],
    answer: "b) 8",
  },
  {
    question:
      'Which word in the following sentence is spelled incorrectly? "The quick brown fox jumps over the lazy dog."',
    options: [
      "a) quick",
      "b) brown",
      "c) fox",
      "d) All words are spelled correctly",
    ],
    answer: "d) All words are spelled correctly",
  },
  {
    question:
      'If you rearrange the letters "DEBIT CARD," you would have a new phrase. What is it?',
    options: [
      "a) Bad Credit",
      "b) Bad Cider",
      "c) Bad Cider Tad",
      "d) Bad Cart Ride",
    ],
    answer: "a) Bad Credit",
  },
  {
    question:
      "If you have a basket with 5 eggs and you take away 3, how many will be left?",
    options: ["a) 2", "b) 3", "c) 5", "d) 8"],
    answer: "a) 2",
  },
  {
    question: "What word becomes shorter when you add two letters to it?",
    options: ["a) Short", "b) Longer", "c) Shorter", "d) None of the above"],
    answer: "c) Shorter",
  },
  {
    question:
      'Which word in the following sentence is spelled incorrectly? "He gave me a seperate piece of cake."',
    options: ["a) He", "b) gave", "c) me", "d) seperate"],
    answer: "d) seperate",
  },
  {
    question:
      "If you have a basket with 10 apples and you take away 3, how many will be left?",
    options: ["a) 7", "b) 8", "c) 9", "d) 10"],
    answer: "a) 7",
  },
  {
    question:
      "What is the next letter in the following series? O T T F F S S ?",
    options: ["a) E", "b) S", "c) F", "d) N"],
    answer: "c) F",
  },
  {
    question:
      'If you rearrange the letters "LISTEN," you would have a new phrase. What is it?',
    options: ["a) Silent", "b) Tinsel", "c) Enlist", "d) Inlets"],
    answer: "c) Enlist",
  },
];

export default function Quiz() {
  const location = useLocation();
  const {
    duration = 10,
    negativeMarking,
    multipleCorrect,
  } = location.state || {};
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [globalTimeRemaining, setGlobalTimeRemaining] = useState(duration * 60); // 10 minutes
  const [questionTimeRemaining, setQuestionTimeRemaining] = useState(60); // 1 minute per question
  const [answers, setAnswers] = useState(Array(questions.length).fill(null));

  useEffect(() => {
    const savedAnswers =
      JSON.parse(localStorage.getItem("answers")) ||
      Array(questions.length).fill(null);
    const savedGlobalTime = parseInt(
      localStorage.getItem("globalTimeRemaining"),
      10
    );
    const savedQuestionTime = parseInt(
      localStorage.getItem("questionTimeRemaining"),
      10
    );

    if (savedGlobalTime && savedGlobalTime > 0) {
      setGlobalTimeRemaining(savedGlobalTime);
      setAnswers(savedAnswers);
      setQuestionTimeRemaining(savedQuestionTime || 60);
    } else {
      localStorage.setItem("globalTimeRemaining", duration * 60);
      localStorage.setItem("questionTimeRemaining", 60);
      localStorage.setItem(
        "answers",
        JSON.stringify(Array(questions.length).fill(null))
      );
    }

    const globalTimer = setInterval(() => {
      setGlobalTimeRemaining((prev) => {
        if (prev <= 1) {
          handleSubmit();
          clearInterval(globalTimer);
          return 0;
        } else {
          localStorage.setItem("globalTimeRemaining", prev - 1);
          return prev - 1;
        }
      });
    }, 1000);

    const questionTimer = setInterval(() => {
      setQuestionTimeRemaining((prev) => {
        if (prev <= 1) {
          handleNextQuestion();
          return 60;
        } else {
          localStorage.setItem("questionTimeRemaining", prev - 1);
          return prev - 1;
        }
      });
    }, 1000);

    return () => {
      clearInterval(globalTimer);
      clearInterval(questionTimer);
    };
  }, [duration]);

  useEffect(() => {
    localStorage.setItem("answers", JSON.stringify(answers));
  }, [answers]);

  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const handleNextQuestion = () => {
    setAnswers((prev) => {
      const newAnswers = [...prev];
      newAnswers[currentQuestion] = selectedOption;
      return newAnswers;
    });
    setSelectedOption("");

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      handleSubmit();
    }
    setQuestionTimeRemaining(60);
  };

  const handleSubmit = () => {
    const correctAnswers = questions.filter(
      (question, index) => question.answer === answers[index]
    ).length;
    console.log(
      `You got ${correctAnswers} out of ${questions.length} correct.`
    );
  };

  return (
    <>
      <div className="quiz-container">
        <div className="quiz-header">
          <p>Duration: {duration} minutes</p>
          <p>Negative Marking: {negativeMarking ? "Yes" : "No"}</p>
          <p>Multiple Correct: {multipleCorrect ? "Yes" : "No"}</p>
          <p className="global-timer">
            Global Time remaining: {Math.floor(globalTimeRemaining / 60)}:
            {("0" + (globalTimeRemaining % 60)).slice(-2)}
          </p>
          <p className="question-timer">
            Question Time remaining: {Math.floor(questionTimeRemaining / 60)}:
            {("0" + (questionTimeRemaining % 60)).slice(-2)}
          </p>
        </div>
        <h3>{questions[currentQuestion].question}</h3>
        {questions[currentQuestion].options.map((option, index) => (
          <div key={index}>
            <input
              type="radio"
              name="option"
              value={option}
              checked={selectedOption === option}
              onChange={handleOptionChange}
            />
            <label>{option}</label>
          </div>
        ))}
        <button onClick={handleNextQuestion}>
          {currentQuestion === questions.length - 1 ? "Submit Quiz" : "Next"}
        </button>
      </div>
    </>
  );
}
