import React, { useState, useEffect } from "react";

const Quiz = () => {
  const [questions, setQuestions] = useState([
    {
      id: 1,
      question:
        "If you have a bowl with 6 apples and you take away 3, how many do you have left?",
      options: ["a) 3", "b) 6", "c) 2", "d) 4"],
      answer: "a) 3",
    },
    {
      id: 2,
      question:
        "A farmer has 15 cows and all but 8 die. How many cows are left?",
      options: ["a) 7", "b) 8", "c) 15", "d) 23"],
      answer: "a) 7",
    },
    {
      id: 3,
      question: "What word becomes shorter when you add two letters to it?",
      options: ["a) Short", "b) Longer", "c) Shorter", "d) None of the above"],
      answer: "a) Short",
    },
  ]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [timeRemaining, setTimeRemaining] = useState(60);
  const [sessionActive, setSessionActive] = useState(false);
  const [quizState, setQuizState] = useState(
    Array(questions.length).fill(null)
  );
  const [score, setScore] = useState(0);
  const [questionStatus, setQuestionStatus] = useState(
    Array(questions.length).fill("notAttempted")
  );
  const [showSummary, setShowSummary] = useState(false);

  useEffect(() => {
    const handleBeforeUnload = (event) => {
      event.preventDefault();
      event.returnValue =
        "Your progress will be lost and the quiz will be submitted. Are you sure you want to reload?";
    };

    window.addEventListener("beforeunload", handleBeforeUnload);

    const sessionTimer = setTimeout(() => {
      setSessionActive(false);
      alert("Session ended. Please start again to attempt the quiz.");
    }, 10 * 60 * 1000); // 10 minutes session

    return () => {
      clearTimeout(sessionTimer);
      window.removeEventListener("beforeunload", handleBeforeUnload);
    };
  }, []);

  useEffect(() => {
    if (sessionActive) {
      const timer = setInterval(() => {
        setTimeRemaining((prev) => {
          if (prev === 0) {
            if (currentQuestion === questions.length - 1) {
              handleSubmit();
            } else {
              handleNextQuestion(true);
            }
            return 60;
          } else {
            return prev - 1;
          }
        });
      }, 1000);

      return () => clearInterval(timer);
    }
  }, [sessionActive, currentQuestion]);

  const handleStartQuiz = () => {
    setSessionActive(true);
  };

  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const handleNextQuestion = (missed = false) => {
    const correctAnswer = questions[currentQuestion].answer;
    const isCorrect = selectedOption === correctAnswer;
    const marks = isCorrect ? 1 : 0;

    const updatedQuizState = [...quizState];
    updatedQuizState[currentQuestion] = {
      question: questions[currentQuestion].question,
      selectedOption,
      correctAnswer,
      marks,
    };

    setQuizState(updatedQuizState);

    setScore((prevScore) => prevScore + marks);

    setSelectedOption("");

    const updatedStatus = [...questionStatus];
    updatedStatus[currentQuestion] =
      selectedOption !== "" ? "attempted" : missed ? "missed" : "notAttempted";
    setQuestionStatus(updatedStatus);

    if (currentQuestion === questions.length - 1) {
      // Last question, handle separately
      handleSubmit();
    } else {
      setCurrentQuestion((prevQuestion) => prevQuestion + 1);
      setTimeRemaining(60); // Reset timer for the next question
    }
  };

  const handleSubmit = () => {
    const correctAnswer = questions[currentQuestion].answer;
    const isCorrect = selectedOption === correctAnswer;
    const marks = isCorrect ? 1 : 0;

    const updatedQuizState = [...quizState];
    updatedQuizState[currentQuestion] = {
      question: questions[currentQuestion].question,
      selectedOption,
      correctAnswer,
      marks,
    };

    setQuizState(updatedQuizState);

    setScore((prevScore) => prevScore + marks);

    setSelectedOption("");

    const updatedStatus = [...questionStatus];
    updatedStatus[currentQuestion] =
      selectedOption !== "" ? "attempted" : "notAttempted";
    setQuestionStatus(updatedStatus);

    setSessionActive(false);
    setShowSummary(true);
  };

  const handleFinalSubmit = () => {
    setShowSummary(false);
    console.log("Quiz State:", quizState);
    console.log("Total Score:", score);
    alert("Quiz submitted successfully. Session ended.");
  };

  return (
    <div className="container">
      <center>
        {!sessionActive && !showSummary && (
            <>
            </>
        )}
      </center>

      {sessionActive && (
        <div className="mt-3">
          <div className="mb-3">
            <p>
              Time Remaining: {`01:${String(timeRemaining).padStart(2, "0")}`}
            </p>
          </div>
          <div>
            <h5>{questions[currentQuestion].question}</h5>
            <form>
              {questions[currentQuestion].options.map((option, index) => (
                <div key={index} className="form-check">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="option"
                    id={`option${index}`}
                    value={option}
                    onChange={handleOptionChange}
                    checked={selectedOption === option}
                  />
                  <label
                    className="form-check-label"
                    htmlFor={`option${index}`}
                  >
                    {option}
                  </label>
                </div>
              ))}
            </form>
          </div>
          <button
            className="btn btn-primary mt-3 me-2"
            onClick={handleNextQuestion}
            disabled={
              selectedOption === "" || currentQuestion === questions.length - 1
            }
          >
            Next
          </button>
          {currentQuestion === questions.length - 1 && (
            <button
              className="btn btn-primary mt-3 ms-2"
              onClick={handleSubmit}
              disabled={selectedOption === ""}
            >
              Submit Quiz
            </button>
          )}
          <div className="mt-3">
            <h5>Question Status:</h5>
            <div className="list-group">
              {questions.map((_, index) => (
                <div
                  key={index}
                  className={`list-group-item ${
                    questionStatus[index] === "attempted"
                      ? "list-group-item-success"
                      : questionStatus[index] === "missed"
                      ? "list-group-item-warning"
                      : ""
                  }`}
                >
                  Question {index + 1} - {questionStatus[index]}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
      {showSummary && (
        <div className="mt-3">
          <h3>Quiz Summary</h3>
          <p>Total Score: {score}</p>
          <p>
            {questionStatus.map((status, index) => (
              <span key={index}>
                Question {index + 1}: {status}
                <br />
              </span>
            ))}
          </p>
          <button className="btn btn-primary" onClick={handleFinalSubmit}>
            Final Submit
          </button>
        </div>
      )}
    </div>
  );
};

export default Quiz;
