import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const questions = [
  {
    question:
      "If you have a bowl with 6 apples and you take away 3, how many do you have left?",
    options: ["a) 3", "b) 6", "c) 2", "d) 4"],
    answer: "a) 3",
  },
  {
    question: "A farmer has 15 cows and all but 8 die. How many cows are left?",
    options: ["a) 7", "b) 8", "c) 15", "d) 23"],
    answer: "b) 8",
  },
  {
    question:
      'Which word in the following sentence is spelled incorrectly? "The quick brown fox jumps over the lazy dog."',
    options: [
      "a) quick",
      "b) brown",
      "c) fox",
      "d) All words are spelled correctly",
    ],
    answer: "d) All words are spelled correctly",
  },
  {
    question:
      'If you rearrange the letters "DEBIT CARD," you would have a new phrase. What is it?',
    options: [
      "a) Bad Credit",
      "b) Bad Cider",
      "c) Bad Cider Tad",
      "d) Bad Cart Ride",
    ],
    answer: "b) Bad Cider",
  },
  {
    question:
      "If you have a basket with 5 eggs and you take away 3, how many will be left?",
    options: ["a) 2", "b) 3", "c) 5", "d) 8"],
    answer: "a) 2",
  },
  {
    question: "What word becomes shorter when you add two letters to it?",
    options: ["a) Short", "b) Longer", "c) Shorter", "d) None of the above"],
    answer: "c) Shorter",
  },
  {
    question:
      'Which word in the following sentence is spelled incorrectly? "He gave me a seperate piece of cake."',
    options: ["a) He", "b) gave", "c) me", "d) seperate"],
    answer: "d) seperate",
  },
  {
    question:
      "If you have a basket with 10 apples and you take away 3, how many will be left?",
    options: ["a) 7", "b) 8", "c) 9", "d) 10"],
    answer: "a) 7",
  },
  {
    question:
      "What is the next letter in the following series? O T T F F S S ?",
    options: ["a) E", "b) S", "c) F", "d) N"],
    answer: "c) F",
  },
  {
    question:
      'If you rearrange the letters "LISTEN," you would have a new phrase. What is it?',
    options: ["a) Silent", "b) Tinsel", "c) Enlist", "d) Inlets"],
    answer: "c) Enlist",
  },
];

export default function Quiz() {
  const location = useLocation();
  const {
    duration = 10,
    negativeMarking,
    multipleCorrect,
  } = location.state || {};
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [questionTimeRemaining, setQuestionTimeRemaining] = useState(60); // 1 minute per question
  const [answers, setAnswers] = useState(Array(questions.length).fill(""));
  const [score, setScore] = useState(0);
  const [quizState, setQuizState] = useState([]);
  const [questionStatus, setQuestionStatus] = useState(
    Array(questions.length).fill("notAttempted")
  );

  useEffect(() => {
    const savedCurrentQuestion =
      parseInt(localStorage.getItem("currentQuestion"), 10) || 0;
    const savedSelectedOption = localStorage.getItem("selectedOption") || "";
    const savedAnswers =
      JSON.parse(localStorage.getItem("answers")) ||
      Array(questions.length).fill("");
    const savedQuizState = JSON.parse(localStorage.getItem("quizState")) || [];
    const savedQuestionStatus =
      JSON.parse(localStorage.getItem("questionStatus")) ||
      Array(questions.length).fill("notAttempted");
    const savedScore = parseInt(localStorage.getItem("score"), 10) || 0;

    setCurrentQuestion(savedCurrentQuestion);
    setSelectedOption(savedSelectedOption);
    setAnswers(savedAnswers);
    setQuizState(savedQuizState);
    setQuestionStatus(savedQuestionStatus);
    setScore(savedScore);
  }, []);

  useEffect(() => {
    localStorage.setItem("currentQuestion", currentQuestion);
    localStorage.setItem("selectedOption", selectedOption);
    localStorage.setItem("answers", JSON.stringify(answers));
    localStorage.setItem("quizState", JSON.stringify(quizState));
    localStorage.setItem("questionStatus", JSON.stringify(questionStatus));
    localStorage.setItem("score", score);
  }, [
    currentQuestion,
    selectedOption,
    answers,
    quizState,
    questionStatus,
    score,
  ]);

  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const handleNextQuestion = (missed = false) => {
    // Your existing handleNextQuestion code...
  };

  const handleQuestionClick = (index) => {
    setCurrentQuestion(index);
    setQuestionTimeRemaining(60);
  };

  const handleSubmit = (finalQuizState = quizState) => {
    console.log("Quiz State:", finalQuizState);
    console.log(`Total Marks: ${score}`);
  };

  return (
    <>
      <div className="quiz-container">
        <div className="question-container">
          <p>{questions[currentQuestion].question}</p>
          <ul>
            {questions[currentQuestion].options.map((option, index) => (
              <li key={index}>
                <input
                  type="radio"
                  id={`option${index}`}
                  name="options"
                  value={option}
                  checked={selectedOption === option}
                  onChange={handleOptionChange}
                />
                <label htmlFor={`option${index}`}>{option}</label>
              </li>
            ))}
          </ul>
          <div>
            <button onClick={() => handleNextQuestion()}>Next</button>
          </div>
        </div>
        <div className="question-navigation">
          {questions.map((question, index) => (
            <button
              key={index}
              className={currentQuestion === index ? "active" : ""}
              onClick={() => handleQuestionClick(index)}
            >
              {index + 1}
            </button>
          ))}
        </div>
      </div>
    </>
  );
}
